import React from "react";
import './Form.css'
import Card from "../UI/Card";

const submitHandler =(e) =>{
    e.preventDefault();

    console.log()
}


const Form= () => {
    return(
        <Card>
             <form id="form" onSubmit={submitHandler}>

<div className="name">
<label>UserName</label> <br/>
<input type="text"/>
<div/>
<div className="age">
<label>Age (Years)</label> <br/>
<input type="number"/>
</div>
<div>
    <button id="btn" type="submit">Add User</button>
</div>

</div>


</form>

        </Card>
   

)}

export default Form;
